    private void jPasswordField1KeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jPasswordField1KeyPressed
        if (evt.getKeyCode() == KeyEvent.VK_ENTER) {
            Logica.Conexion1.setcuenta(jTextField1.getText(), jPasswordField1.getText());
            Logica.Conexion1.getConexion();
            if (Logica.Conexion1.getstatus()) {
               frminicio form = new frminicio();
                form.toFront();
                form.setVisible(true);
                  this.dispose();

            } else {
                JOptionPane.showMessageDialog(null, "Usuario y password incorecto", "Error de conexion", JOptionPane.ERROR_MESSAGE);
                jTextField1.setText("");
                jPasswordField1.setText("");
            }
        }
    }//GEN-LAST:event_jPasswordField1KeyPressed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        Logica.Conexion1.setcuenta(jTextField1.getText(), jPasswordField1.getText());
        Logica.Conexion1.getConexion();
        if (Logica.Conexion1.getstatus()) {
            frminicio form = new frminicio();
                form.toFront();
                form.setVisible(true);

        } else {
            JOptionPane.showMessageDialog(null, "Usuario y password incorecto", "Error de conexion", JOptionPane.ERROR_MESSAGE);
            jTextField1.setText("");
            jPasswordField1.setText("");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

